import React from 'react';
import { Modal } from 'reactstrap';
import { UserSession } from '../App';

interface AttendanceProps {
    userSession: UserSession;
    setUserSession: (userSession: UserSession) => void;
}

const Attendance = ({userSession,setUserSession}:AttendanceProps) => {
    const punchInHandler = () => {
        const punches = JSON.parse(localStorage.getItem("punches") || '{}')
        const currentEmployee = punches["emp_lubina"] || {}
        const punchInTimeDate = new Date();
        currentEmployee[punchInTimeDate.toISOString().split("T")[0]] = {
            punchInTimeDate,
        }
        punches["emp_lubina"] = currentEmployee;
        localStorage.setItem("punches", JSON.stringify(punches));
        setUserSession({...userSession, punchInDateTime: punchInTimeDate.toISOString()});
    }

    const [openModal, setModal] = React.useState(false);
    const [text, setText] = React.useState("");
    const handleText = (e: React.ChangeEvent<HTMLInputElement>) => {
        setText(e.target.value)
    }

    const handleSave = (e: any) => {
        const punches = JSON.parse(localStorage.getItem("punches") || "{}")
        const currentEmployee = punches["emp_lubina"] || {}
        const punchOutTimeDate = new Date();
        currentEmployee[punchOutTimeDate.toISOString().split("T")[0]] = {
            punchOutTimeDate,
        }
        punches["emp_lubina"] = currentEmployee;
        localStorage.setItem("punches", JSON.stringify(punches));
        setUserSession({...userSession, punchOutDateTime: punchOutTimeDate.toISOString()});

    }


    return (
        <div>
            <button onClick={punchInHandler} 
             disabled={!!userSession.punchInDateTime}>Punch In</button>
            <button  disabled={!userSession.punchInDateTime||!!userSession.punchOutDateTime} 
             onClick={() => setModal(!openModal)}> Punch Out</button>
            <Modal isOpen={openModal} size="" >
                Work Updates
                <input onChange={handleText} value={text} />
                <button onClick={handleSave} value="Save">Save</button>
            </Modal>
        </div>
    )

}
export default Attendance;
